import React, { useState, useEffect } from 'react';
import { Chess } from 'chess.js';
import { Chessboard } from 'react-chessboard';
import './Chessboard.css';

interface Props {
  fen: string | null;
  moves: string[];
  onMove: (move: string) => void;
  playerColor: 'w' | 'b' | null;
  turn: 'w' | 'b';
  soloMode?: boolean;
  boardOrientation?: 'white' | 'black';
}

const ChessboardComponent: React.FC<Props> = ({ fen, moves, onMove, playerColor, turn, soloMode, boardOrientation }) => {
  const [game, setGame] = useState(new Chess());
  const [lastMoveSquares, setLastMoveSquares] = useState<{from: string, to: string} | null>(null);
  const [boardWidth, setBoardWidth] = useState<number>(Math.min(window.innerWidth * 0.9, 350));
  const [moveError, setMoveError] = useState<string | null>(null);

  useEffect(() => {
    const handleResize = () => {
      setBoardWidth(Math.min(window.innerWidth * 0.9, 350));
    };
    window.addEventListener('resize', handleResize);
    handleResize();
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Initialize game from fen or reset
  useEffect(() => {
    const newGame = new Chess();
    if (fen && fen !== 'start') {
      newGame.load(fen);
    }
    setGame(newGame);
  }, [fen]);

  // Apply moves from server
  useEffect(() => {
    const newGame = new Chess(game.fen());
    let lastMove: any = null;
    moves.forEach((move) => {
      try {
        lastMove = newGame.move(move);
      } catch (err) {
        console.warn('Invalid move in history:', move, err);
      }
    });
    setGame(newGame);
    if (lastMove && lastMove.from && lastMove.to) {
      setLastMoveSquares({ from: lastMove.from, to: lastMove.to });
    }
  }, [moves]);

  const handlePieceDrop = (sourceSquare: string, targetSquare: string) => {
    // In solo mode, allow both sides to move
    if (!soloMode && (!playerColor || playerColor !== turn)) return false;
    const move = game.move({ from: sourceSquare, to: targetSquare, promotion: 'q' });
    if (move) {
      setMoveError(null);
      onMove(move.san);
      setGame(new Chess(game.fen()));
      setLastMoveSquares({ from: move.from, to: move.to });
      return true;
    } else {
      setMoveError('Illegal move!');
    }
    return false;
  };

  // Custom board colors for masculine theme
  const boardColors = {
    light: '#BCAAA4', // light brown
    dark: '#3E2723',  // dark brown
  };

  // Highlight last move
  const customSquareStyles = lastMoveSquares
    ? {
        [lastMoveSquares.from]: {
          background: '#FFD700',
          boxShadow: '0 0 10px 2px #FFD700',
        },
        [lastMoveSquares.to]: {
          background: '#B22222',
          boxShadow: '0 0 10px 2px #B22222',
        },
      }
    : {};

  return (
    <div className="chessboard-container">
      {moveError && <div style={{ color: 'red', fontWeight: 'bold', marginBottom: 8 }}>{moveError}</div>}
      {playerColor && playerColor !== turn && !soloMode && (
        <div style={{ color: '#FFD700', marginBottom: 8, fontWeight: 'bold' }}>
          Waiting for opponent's move...
        </div>
      )}
      <Chessboard
        position={game.fen()}
        onPieceDrop={handlePieceDrop}
        boardWidth={boardWidth}
        boardOrientation={boardOrientation || (playerColor === 'b' ? 'black' : 'white')}
        customBoardStyle={{
          borderRadius: '8px',
          boxShadow: '0 2px 12px rgba(0,0,0,0.6)',
        }}
        customDarkSquareStyle={{ backgroundColor: boardColors.dark }}
        customLightSquareStyle={{ backgroundColor: boardColors.light }}
        customSquareStyles={customSquareStyles}
        arePiecesDraggable={!!soloMode || (!!playerColor && playerColor === turn)}
      />
    </div>
  );
};

export default ChessboardComponent;
